package com.example.haltura.Adapters


import android.content.res.ColorStateList
import android.graphics.BitmapFactory
import android.os.Build
import android.util.Base64.DEFAULT
import android.util.Base64.decode
import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import android.widget.ImageView
import android.widget.RelativeLayout
import android.widget.TextView
import androidx.annotation.RequiresApi
import androidx.recyclerview.widget.RecyclerView
import com.example.haltura.R
import com.example.haltura.Sql.Items.Chat
import com.example.haltura.Sql.Items.Message


class ChatsAdapter(
    private var _dataSet: List<Chat>,
    private var _currentUserid: String,
    private val _clickOnItemListener: (Chat) -> Unit,
) : RecyclerView.Adapter<ChatsAdapter.ViewHolder>() {

    companion object
    {
        const val USER_NO_PIC = ""
        const val GROUP_NO_PIC = ""
    }


    class ViewHolder(view: View) : RecyclerView.ViewHolder(view) {
        val name: TextView = view.findViewById(R.id.name_chat)
        val message: TextView = view.findViewById(R.id.message_chat) //üñºÔ∏è
        val image: ImageView = view.findViewById(R.id.image_chat)
        val time: TextView = view.findViewById(R.id.time_chat)
    }

//    fun setData(data: MutableList<Message>) {
//        _dataSet = data
//    }

    // Create new views (invoked by the layout manager)
    override fun onCreateViewHolder(viewGroup: ViewGroup, viewType: Int): ViewHolder {
        // Create a new view, which defines the UI of the list item
        val view = LayoutInflater.from(viewGroup.context)
            .inflate(R.layout.user_chat_item, viewGroup, false)

        return ViewHolder(view)
    }

    override fun getItemViewType(position: Int): Int {
        return position
    }

    //@RequiresApi(Build.VERSION_CODES.O)
    override fun onBindViewHolder(viewHolder: ViewHolder, position: Int) {
        // Get element from your dataset at this position and replace the
        // contents of the view with that element
        val currentItem = _dataSet[position]
        val name = currentItem.getName()
        if (name == null)
        {
            var userId = currentItem.getMembers()?.get(0)
            if (userId == _currentUserid)
            {
                userId = currentItem.getMembers()?.get(1)
            }
            //todo: get username from userid
            viewHolder.name.text = "username"
        }
        else
        {
            viewHolder.name.text = name
        }
        val image = currentItem.getImage()
        if (image == null)
        {
            //todo: add user image
            val bm = decode(USER_NO_PIC, DEFAULT)
            val data = BitmapFactory.decodeByteArray(bm, 0, bm.size)
            viewHolder.image.setImageBitmap(data)
        }
        else
        {
            val bm = decode(GROUP_NO_PIC, DEFAULT)
            val data = BitmapFactory.decodeByteArray(bm, 0, bm.size)
            viewHolder.image.setImageBitmap(data)
        }
        val messages = currentItem.getMessages()
        val message: Message? = messages?.get(messages.size-1)
        var txt: String = ""
        if (!(messages == null || messages.size == 0))
        {
            if (name == null) {//(group)
                txt += message?.getName() +": "
            }
            if (message?.getImage() != null)
            {
                txt += "üñºÔ∏è-"
            }
            if (message?.getText() != null)
            {
                //todo cut 20 chars with 3 dots (...)
                txt += message?.getText()
            }
        }
        viewHolder.message.text = txt
        val time = message?.getTime()
        viewHolder.time.text = time.toString()

        viewHolder.itemView.setOnClickListener {
            _clickOnItemListener(_dataSet[position])
        }
    }

    // Return the size of your dataset (invoked by the layout manager)
    override fun getItemCount() = _dataSet.size
}